mapper.select:
select * from emp

    and empno = #{empno}
    and ename = #{ename}
    and job = #{job}
    and mgr = #{mgr}
    and hiredate = #{hiredate}
    and sal = #{sal}
    and comm = #{comm}
    and deptno = #{deptno}

mapper.update:
update emp set
ename = #{ename}, job = #{job}, mgr = #{mgr}, hiredate = #{hiredate}, sal = #{sal}, comm = #{comm}, deptno = #{deptno},
where empno = #{empno}
